mapper.select:
select DISTINCT nickname name,head_url, (select count(DISTINCT openid) from activity_participant where invate_openid=a.openid and activityId=a.draw_activity_id and stype='draw' ) participantCount, a.draw_activity_id activityName from draw_participant a,w_p_fans b
a.openid=b.openid
and a.draw_activity_id=#{activityId}
